//Librerias
import express from 'express';
import { Juego } from './models/Juego.js';
import { Estado, Perfil } from './models/Perfil.js';

const juego1: Juego = {
    id: "1",
    nombre: "Ejemplo",
    descripcion: "Descripción de ejemplo",
    precio: 69,
    fechaLanzamiento: new Date(),
    desarrollador: "Desarrolladora de ejemplo",
    editor: "Editor de ejemplo",
    generos: ["Géneros", "De", "Ejemplo"],
    portadaB64: "",
}
const juego2: Juego = {
    id: "2",
    nombre: "Ejemplo2",
    descripcion: "Me gusta el queso",
    precio: 17,
    fechaLanzamiento: new Date(),
    desarrollador: "Hola",
    editor: "No se que poner",
    generos: ["Que", "Alguien", "Me", "De", "Ideas"],
    portadaB64: "",
}

const perfil1: Perfil = {
    id: '1',
    nombreUsuario: 'Paquito69',
    contrasenya: 'Hola me llamo Paquito',
    estado: Estado.Conectado,
    region: 'España',
    juegosAdquiridos: [juego1, juego2]
}
const perfil2: Perfil = {
    id: '2',
    nombreUsuario: 'Pepito420',
    contrasenya: 'Hola me llamo Pepito',
    estado: Estado.Ausente,
    region: 'Andorra',
    juegosAdquiridos: [juego2, juego1]
}

let app = express();

// Carga de middleware y enrutadores
app.use(express.json());

//Servicios
app.get("/juegos", (_req, res) => {
    
    res.send([juego1, juego2])
});

//Servicios
app.get("/juegos/:id", (_req, res) => {
    res.send(juego1)
});

app.get("/perfiles", (_req, res) => {
    res.send([perfil1, perfil2])
});

app.get("/perfiles/:id", (_req, res) => {
    res.send(perfil2)
});

//Puesta en marcha
app.listen(8080);
console.log("Servicios iniciados");